changeSet.sql:
CREATE TABLE IF NOT EXISTS main.course ( title TEXT NOT NULL, description TEXT NOT NULL, author BIGSERIAL, createdAt TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP, id BIGSERIAL NOT NULL PRIMARY KEY, FOREIGN KEY (author) REFERENCES authentication.user(id) );
CREATE TABLE IF NOT EXISTS main.course_invited_user ( course_id BIGSERIAL, user_id BIGSERIAL, id BIGSERIAL NOT NULL PRIMARY KEY, FOREIGN KEY (course_id) REFERENCES main.course(id), FOREIGN KEY (user_id) REFERENCES authentication.user(id) );
CREATE TABLE IF NOT EXISTS main.contact ( owner BIGSERIAL, contact_id BIGSERIAL, id BIGSERIAL NOT NULL PRIMARY KEY, FOREIGN KEY (owner) REFERENCES authentication.user(id), FOREIGN KEY (contact_id) REFERENCES authentication.user(id) );
CREATE TABLE IF NOT EXISTS main.task ( title TEXT NOT NULL, description TEXT NOT NULL, author BIGSERIAL, createdAt TIMESTAMPTZ NOT NULL DEFAULT CURRENT_TIMESTAMP, deadline TIMESTAMPTZ NOT NULL, course_id BIGSERIAL, id BIGSERIAL NOT NULL PRIMARY KEY, FOREIGN KEY (author) REFERENCES authentication.user(id), FOREIGN KEY (course_id) REFERENCES main.course(id) );
